set names UTF8;
drop database if exists e_book;
create database e_book charset=UTF8;
use e_book;

create table book_user(
   uid int primary key auto_increment,
   uname varchar(10),
   upwd varchar(15)
);

-- 图书类型
create table book_list(
   lid smallint(5) unsigned,
   object varchar(30),
   anothername varchar(20)
);

-- 图书数据
create table book_value(
   vid int primary key auto_increment,
   title varchar(50),
   image varchar(50),
   price decimal(5,2),
   author varchar(30),
   category_id smallint(5),
   time varchar(30),
   Printing varchar(20),
   info varchar(300)
);


